import React, { useEffect, useState } from "react";
import { getFolders, createFolder } from "../services/api";

function FolderPanel({ onSelectFolder }) {
  const [folders, setFolders] = useState([]);
  const [newFolderName, setNewFolderName] = useState("");

  useEffect(() => {
    getFolders().then((data) => setFolders(data));
  }, []);

  const handleCreateFolder = async () => {
    if (!newFolderName.trim()) {
      alert("Folder name is required");
      return;
    }
    const folder = await createFolder(newFolderName);
    setFolders([...folders, folder]);
    setNewFolderName("");
  };
  console.log('folders',folders)
  // Fungsi untuk merender folder dengan dropdown jika ada subfolder
  const renderFolderTree = (folders, parentId = null) => {
    const childFolders = folders.filter((folder) => folder.parent_id === parentId);

    if (childFolders.length === 0) {
      return null;
    }

    return (
      <ul>
        {childFolders.map((folder) => (
          <li key={folder.id}>
            <button onClick={() => onSelectFolder(folder.id)}>{folder.name}</button>
            {/* Rekursi untuk merender child folders */}
            {renderFolderTree(folders, folder.id)}
          </li>
        ))}
      </ul>
    );
  };

  return (
    <div>
      <h3>Folders</h3>
      {/* Memulai rendering folder tree dari root (parent_id = null) */}
      {renderFolderTree(folders)}
      <div>
        <input
          type="text"
          value={newFolderName}
          onChange={(e) => setNewFolderName(e.target.value)}
          placeholder="New folder name"
        />
        <button onClick={handleCreateFolder}>Create Folder</button>
      </div>
    </div>
  );
}

